import "../../App.css";
import Text from './../Text';
import Arrow from '../../downArrow.png';

function Collapse(props) {

    const collapseButtonStyle = {
        width: props.collapsWidth,
        backgroundColor: "#FF6060",
        color: "White",
        width: props.collapseWidth,
        height: "50px",
        borderRadius: "5px",
        border: "none",
        textAlign: "left",
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between"
    }

    const collapseTextStyle = {
        margin: "auto 0",
    }

    const collapseImgStyle = {
        margin: "auto 10px auto 0",
    }

    if (props.contentType === "text") {
        return (
            <div className="collapseContainer">
                <button type="button" className="collapseButton" style={collapseButtonStyle}><h2 className="collapseTitle" style={collapseTextStyle}>testButtonTitle</h2><img src={Arrow} alt="arrow" style={collapseImgStyle} /></button>
                <div className="collapseContent">
                    <Text type="text" content={props.content} weight={props.weight} size={props.size} align={props.align} textAlign={props.textAlign} decoration={props.decoration} color={props.color} />
                </div>
            </div>
        )
    }
    if (props.contentType === "list") {
        
    }
}

export default Collapse;
